cohortCompany.filterCode:
// return row.cohort.companies.companyUser == $getUser('profileID')
const cohortCompanies = []

for (let key in rowData.$cohort.$companies || {}) {
    const company = rowData.$cohort.$companies[key]

    cohortCompanies.push(company.rowKey)
}

return row.owner === fbUser.uid && cohortCompanies.includes(row.rowKey)

